import{_ as i,c as a,o as t,ag as o}from"./chunks/framework.BCYM_D_J.js";const u=JSON.parse('{"title":"Debugging API & CLI-Only Architecture","description":"","frontmatter":{},"headers":[],"relativePath":"Debugging_API.md","filePath":"Debugging_API.md","lastUpdated":1768571710000}'),r={name:"Debugging_API.md"};function s(n,e,l,c,d,h){return t(),a("div",null,[...e[0]||(e[0]=[o('<h1 id="debugging-api-cli-only-architecture" tabindex="-1">Debugging API &amp; CLI-Only Architecture <a class="header-anchor" href="#debugging-api-cli-only-architecture" aria-label="Permalink to &quot;Debugging API &amp; CLI-Only Architecture&quot;">​</a></h1><h2 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-label="Permalink to &quot;Overview&quot;">​</a></h2><p>This document defines the &quot;Debugging API&quot; approach for SwitchCraft (formerly referred to as Google Antigravity internally). By decoupling core logic from the interface, we enable high-speed iteration and AI-optimized troubleshooting.</p><h2 id="architecture" tabindex="-1">Architecture <a class="header-anchor" href="#architecture" aria-label="Permalink to &quot;Architecture&quot;">​</a></h2><ul><li><strong>Core Logic</strong>: Independent module containing all functional code (Analyzers, Services, Utilities).</li><li><strong>GUI Layer</strong>: Optional wrapper (<code>switchcraft.gui</code>) for visual interaction using CustomTkinter.</li><li><strong>CLI Layer</strong>: Direct interface (<code>switchcraft.main</code>) to the Core Logic for headless operations.</li></ul><h2 id="build-targets" tabindex="-1">Build Targets <a class="header-anchor" href="#build-targets" aria-label="Permalink to &quot;Build Targets&quot;">​</a></h2><ol><li><strong>Standard (Default)</strong>: Includes GUI + CLI. Distributes as <code>SwitchCraft.exe</code>. Best for end-users.</li><li><strong>CLI-Only</strong>: <ul><li>Triggered via <code>BUILD_CLI_ONLY=1</code> environment variable during PyInstaller build.</li><li>Excludes <code>switchcraft.gui</code>, <code>customtkinter</code>, <code>tkinter</code>, <code>PIL</code>, etc.</li><li>Faster compilation and minimal disk footprint.</li><li>Used for &quot;Debugging API&quot; workflows.</li><li>Distributes as <code>SwitchCraft-CLI.exe</code>.</li></ul></li></ol><h2 id="ai-optimized-debugging-the-api" tabindex="-1">AI-Optimized Debugging (The API) <a class="header-anchor" href="#ai-optimized-debugging-the-api" aria-label="Permalink to &quot;AI-Optimized Debugging (The API)&quot;">​</a></h2><p>The CLI-only version acts as a Debugging API. If a crash or functional error occurs, an AI agent can:</p><ol><li><strong>Execute commands via CLI</strong>: <code>SwitchCraft-CLI.exe &lt;installer_path&gt; --json</code></li><li><strong>Parse verbose English logs</strong>: Structured JSON output ensures machine readability.</li><li><strong>Verify Fixes</strong>: Recompile only the CLI target to verify logic changes in seconds.</li></ol><h2 id="automated-releases" tabindex="-1">Automated Releases <a class="header-anchor" href="#automated-releases" aria-label="Permalink to &quot;Automated Releases&quot;">​</a></h2><p>GitHub Actions is configured to provide:</p><ul><li><code>SwitchCraft.exe</code> (GUI + CLI)</li><li><code>SwitchCraft-CLI.exe</code> (CLI only, optimized for speed)</li></ul><h2 id="usage" tabindex="-1">Usage <a class="header-anchor" href="#usage" aria-label="Permalink to &quot;Usage&quot;">​</a></h2><h3 id="running-cli-check" tabindex="-1">Running CLI Check <a class="header-anchor" href="#running-cli-check" aria-label="Permalink to &quot;Running CLI Check&quot;">​</a></h3><div class="language-powershell vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">powershell</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;">SwitchCraft-CLI.exe</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> &quot;C:\\Path\\To\\Setup.exe&quot;</span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;"> --</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">json</span></span></code></pre></div><h3 id="diagnostic-mode" tabindex="-1">Diagnostic Mode <a class="header-anchor" href="#diagnostic-mode" aria-label="Permalink to &quot;Diagnostic Mode&quot;">​</a></h3><div class="language-powershell vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">powershell</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;">SwitchCraft-CLI.exe</span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;"> --</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">diagnose</span></span></code></pre></div><p>(Prints dependency status, config loaded, and environment info)</p>',19)])])}const p=i(r,[["render",s]]);export{u as __pageData,p as default};
