import{_ as a,o,c as e,ag as i}from"./chunks/framework.WsmfbX9q.js";const g=JSON.parse('{"title":"Installing SwitchCraft as an App (PWA)","description":"","frontmatter":{},"headers":[],"relativePath":"pwa_installation.md","filePath":"pwa_installation.md","lastUpdated":1769205918000}'),r={name:"pwa_installation.md"};function n(s,t,l,h,c,d){return o(),e("div",null,[...t[0]||(t[0]=[i('<h1 id="installing-switchcraft-as-an-app-pwa" tabindex="-1">Installing SwitchCraft as an App (PWA) <a class="header-anchor" href="#installing-switchcraft-as-an-app-pwa" aria-label="Permalink to &quot;Installing SwitchCraft as an App (PWA)&quot;">​</a></h1><p>SwitchCraft is a modern web application that supports <strong>Progressive Web App (PWA)</strong> technology. This allows you to install it on your device and use it like a native application, without going through an app store.</p><h2 id="prerequisites" tabindex="-1">Prerequisites <a class="header-anchor" href="#prerequisites" aria-label="Permalink to &quot;Prerequisites&quot;">​</a></h2><p>To install SwitchCraft, you need access to one of the following:</p><ol><li><strong>SwitchCraft Demo</strong>: The official demo hosted on GitHub Pages.</li><li><strong>Self-Hosted SwitchCraft</strong>: A running Docker instance of SwitchCraft (Web Mode).</li></ol><h2 id="installation-steps" tabindex="-1">Installation Steps <a class="header-anchor" href="#installation-steps" aria-label="Permalink to &quot;Installation Steps&quot;">​</a></h2><h3 id="android-chrome-edge" tabindex="-1">Android (Chrome / Edge) <a class="header-anchor" href="#android-chrome-edge" aria-label="Permalink to &quot;Android (Chrome / Edge)&quot;">​</a></h3><ol><li>Open the SwitchCraft URL in <strong>Google Chrome</strong> or <strong>Microsoft Edge</strong>.</li><li>Tap the <strong>Examples</strong> (three dots) menu in the top right corner.</li><li>Tap <strong>&quot;Install App&quot;</strong> or <strong>&quot;Add to Home screen&quot;</strong>.</li><li>Confirm the installation by tapping <strong>&quot;Install&quot;</strong>.</li><li>Wait a moment. The <strong>SwitchCraft</strong> icon will appear on your home screen and app drawer.</li></ol><h3 id="ios-ipados-safari" tabindex="-1">iOS / iPadOS (Safari) <a class="header-anchor" href="#ios-ipados-safari" aria-label="Permalink to &quot;iOS / iPadOS (Safari)&quot;">​</a></h3><ol><li>Open the SwitchCraft URL in <strong>Safari</strong>.</li><li>Tap the <strong>Share</strong> button (rectangle with an arrow pointing up) at the bottom of the screen.</li><li>Scroll down and tap <strong>&quot;Add to Home Screen&quot;</strong>.</li><li>Review the name (e.g., &quot;SwitchCraft Demo&quot;) and tap <strong>&quot;Add&quot;</strong> in the top right corner.</li><li>The app icon will appear on your home screen.</li></ol><h3 id="windows-macos-linux-chrome-edge" tabindex="-1">Windows / macOS / Linux (Chrome / Edge) <a class="header-anchor" href="#windows-macos-linux-chrome-edge" aria-label="Permalink to &quot;Windows / macOS / Linux (Chrome / Edge)&quot;">​</a></h3><ol><li>Open the SwitchCraft URL in <strong>Google Chrome</strong> or <strong>Microsoft Edge</strong>.</li><li>Look for the <strong>&quot;Install SwitchCraft&quot;</strong> icon (a computer with a down arrow) in the address bar (right side).</li><li>Click the icon and select <strong>&quot;Install&quot;</strong>.</li><li>The app will launch in its own window. You can pin it to your Taskbar (Windows) or Dock (macOS) for easy access.</li></ol><h2 id="features" tabindex="-1">Features <a class="header-anchor" href="#features" aria-label="Permalink to &quot;Features&quot;">​</a></h2><ul><li><strong>Standalone Experience</strong>: Runs without the browser address bar and controls.</li><li><strong>Fast Access</strong>: Launch directly from your home screen or desktop.</li><li><strong>Always Up-to-Date</strong>: The app automatically uses the latest version from your server.</li></ul>',14)])])}const u=a(r,[["render",n]]);export{g as __pageData,u as default};
