import{_ as t,o as a,c as i,ag as o}from"./chunks/framework.BIxokheY.js";const g=JSON.parse('{"title":"SwitchCraft CloudSync","description":"","frontmatter":{},"headers":[],"relativePath":"CLOUDSYNC.md","filePath":"CLOUDSYNC.md","lastUpdated":1769266770000}'),r={name:"CLOUDSYNC.md"};function s(n,e,c,l,d,u){return a(),i("div",null,[...e[0]||(e[0]=[o('<h1 id="switchcraft-cloudsync" tabindex="-1">SwitchCraft CloudSync <a class="header-anchor" href="#switchcraft-cloudsync" aria-label="Permalink to &quot;SwitchCraft CloudSync&quot;">​</a></h1><p>SwitchCraft uses <strong>GitHub Gists</strong> to securely synchronize your settings across devices.</p><h2 id="how-it-works" tabindex="-1">How it works <a class="header-anchor" href="#how-it-works" aria-label="Permalink to &quot;How it works&quot;">​</a></h2><ol><li><strong>Authentication</strong>: SwitchCraft uses the secure <a href="https://docs.github.com/en/apps/oauth-apps/building-oauth-apps/authorizing-oauth-apps#device-flow" target="_blank" rel="noreferrer">OAuth 2.0 Device Authorization Grant</a> flow. You never enter your password directly into SwitchCraft.</li><li><strong>Permissions</strong>: We request the minimum necessary permissions: <ul><li><code>gist</code>: To create (if it doesn&#39;t exist) and update a private Gist named <code>switchcraft_settings.json</code>.</li><li><code>read:user</code>: To display your GitHub username in the application.</li></ul></li><li><strong>Storage</strong>: Your settings are stored in a <strong>Secret Gist</strong>. <ul><li>Secret Gists are not searchable and are only accessible by you (and anyone you share the direct link with).</li><li>SwitchCraft only reads/writes the specific file <code>switchcraft_settings.json</code>.</li></ul></li></ol><h2 id="what-data-is-synced" tabindex="-1">What data is synced? <a class="header-anchor" href="#what-data-is-synced" aria-label="Permalink to &quot;What data is synced?&quot;">​</a></h2><ul><li><strong>Application Preferences</strong>: Theme, Language, default paths.</li><li><strong>Intune Configuration</strong>: Tenant ID (if saved), default groups.</li><li><strong>Winget Settings</strong>: Custom sources lists.</li><li><strong>API Keys</strong>: <strong>Note:</strong> For security reasons, highly sensitive secrets like Client Secrets may NOT be synced or are encrypted (depending on configuration). We recommend re-entering sensitive credentials on each new device.</li></ul><h2 id="troubleshooting-login" tabindex="-1">Troubleshooting Login <a class="header-anchor" href="#troubleshooting-login" aria-label="Permalink to &quot;Troubleshooting Login&quot;">​</a></h2><ul><li>If the browser doesn&#39;t open automatically, copy the link and code provided in the dialog.</li><li>Ensure you are logged into the correct GitHub account in your default browser.</li><li>If synchronization fails, check your internet connection and ensure GitHub is accessible.</li></ul><h2 id="data-privacy" tabindex="-1">Data Privacy <a class="header-anchor" href="#data-privacy" aria-label="Permalink to &quot;Data Privacy&quot;">​</a></h2><p>SwitchCraft connects directly to GitHub&#39;s API. No intermediate servers are used. Your data stays between you and GitHub.</p>',10)])])}const p=t(r,[["render",s]]);export{g as __pageData,p as default};
