import{_ as t,o as i,c as a,ag as s}from"./chunks/framework.BIxokheY.js";const p=JSON.parse('{"title":"Registry Reference","description":"","frontmatter":{},"headers":[],"relativePath":"Registry.md","filePath":"Registry.md","lastUpdated":1769271300000}'),l={name:"Registry.md"};function n(d,e,r,o,c,h){return i(),a("div",null,[...e[0]||(e[0]=[s('<h1 id="registry-reference" tabindex="-1">Registry Reference <a class="header-anchor" href="#registry-reference" aria-label="Permalink to &quot;Registry Reference&quot;">​</a></h1><p>SwitchCraft uses the Windows Registry to store user settings and configuration.</p><h2 id="registry-hierarchy-precedence" tabindex="-1">Registry Hierarchy &amp; Precedence <a class="header-anchor" href="#registry-hierarchy-precedence" aria-label="Permalink to &quot;Registry Hierarchy &amp; Precedence&quot;">​</a></h2><p>SwitchCraft reads configuration from several locations in order of precedence (highest priority first):</p><ol><li><strong>Machine Policy</strong> (<code>HKLM\\Software\\Policies\\FaserF\\SwitchCraft</code>) - Enforced by GPO/Intune (Device).</li><li><strong>User Policy</strong> (<code>HKCU\\Software\\Policies\\FaserF\\SwitchCraft</code>) - Enforced by GPO/Intune (User).</li><li><strong>Intune OMA-URI</strong> (<code>HKLM\\Software\\Microsoft\\PolicyManager\\current\\device\\FaserF~SwitchCraft</code>) - Native Intune Policy.</li><li><strong>User Preference</strong> (<code>HKCU\\Software\\FaserF\\SwitchCraft</code>) - Default user settings via UI.</li><li><strong>Machine Preference</strong> (<code>HKLM\\Software\\FaserF\\SwitchCraft</code>) - Machine-wide defaults.</li></ol><div class="note custom-block github-alert"><p class="custom-block-title">NOTE</p><p>Policy keys are read-only for the application. User Preferences are writable by the application.</p></div><h2 id="registry-keys-configuration" tabindex="-1">Registry Keys (Configuration) <a class="header-anchor" href="#registry-keys-configuration" aria-label="Permalink to &quot;Registry Keys (Configuration)&quot;">​</a></h2><table tabindex="0"><thead><tr><th style="text-align:left;">Value Name</th><th style="text-align:left;">Type</th><th style="text-align:left;">Description</th><th style="text-align:left;">Default</th></tr></thead><tbody><tr><td style="text-align:left;"><code>InstallPath</code></td><td style="text-align:left;">REG_SZ</td><td style="text-align:left;">Installation directory (set by installer)</td><td style="text-align:left;">-</td></tr><tr><td style="text-align:left;"><code>Version</code></td><td style="text-align:left;">REG_SZ</td><td style="text-align:left;">Installed version number</td><td style="text-align:left;">-</td></tr><tr><td style="text-align:left;"><code>DebugMode</code></td><td style="text-align:left;">REG_DWORD</td><td style="text-align:left;">Enable verbose logging (1 = enabled, 0 = disabled)</td><td style="text-align:left;"><code>0</code></td></tr><tr><td style="text-align:left;"><code>UpdateChannel</code></td><td style="text-align:left;">REG_SZ</td><td style="text-align:left;">Update channel: <code>stable</code>, <code>beta</code>, or <code>dev</code></td><td style="text-align:left;"><code>stable</code></td></tr><tr><td style="text-align:left;"><code>SkippedVersion</code></td><td style="text-align:left;">REG_SZ</td><td style="text-align:left;">Version that user chose to skip updates for</td><td style="text-align:left;">-</td></tr><tr><td style="text-align:left;"><code>ThemeMode</code></td><td style="text-align:left;">REG_SZ</td><td style="text-align:left;">UI Theme: <code>system</code>, <code>light</code>, <code>dark</code></td><td style="text-align:left;"><code>system</code></td></tr><tr><td style="text-align:left;"><code>Language</code></td><td style="text-align:left;">REG_SZ</td><td style="text-align:left;">UI Language: <code>en</code>, <code>de</code></td><td style="text-align:left;"><code>en</code></td></tr><tr><td style="text-align:left;"><code>CompanyName</code></td><td style="text-align:left;">REG_SZ</td><td style="text-align:left;">Company Name used in packaging meta</td><td style="text-align:left;">-</td></tr><tr><td style="text-align:left;"><code>IntuneTenantId</code></td><td style="text-align:left;">REG_SZ</td><td style="text-align:left;">Microsoft Entra Tenant ID (GUID)</td><td style="text-align:left;">-</td></tr><tr><td style="text-align:left;"><code>IntuneClientId</code></td><td style="text-align:left;">REG_SZ</td><td style="text-align:left;">Application Client ID (GUID)</td><td style="text-align:left;">-</td></tr><tr><td style="text-align:left;"><code>EnableWinget</code></td><td style="text-align:left;">REG_DWORD</td><td style="text-align:left;">Enable Winget Store integration (1/0)</td><td style="text-align:left;"><code>1</code></td></tr><tr><td style="text-align:left;"><code>SignScripts</code></td><td style="text-align:left;">REG_DWORD</td><td style="text-align:left;">Automatically sign PowerShell scripts (1/0)</td><td style="text-align:left;"><code>0</code></td></tr><tr><td style="text-align:left;"><code>AIProvider</code></td><td style="text-align:left;">REG_SZ</td><td style="text-align:left;">AI Backend: <code>openai</code>, <code>gemini</code>, <code>local</code></td><td style="text-align:left;"><code>openai</code></td></tr></tbody></table><h2 id="secure-secrets-keyring" tabindex="-1">Secure Secrets (Keyring) <a class="header-anchor" href="#secure-secrets-keyring" aria-label="Permalink to &quot;Secure Secrets (Keyring)&quot;">​</a></h2><p>Sensitive values like Client Secrets and PATs are stored in the Windows Credential Manager (or encrypted in Registry if policies are used).</p><table tabindex="0"><thead><tr><th style="text-align:left;">Secret Key</th><th style="text-align:left;">Description</th></tr></thead><tbody><tr><td style="text-align:left;"><code>IntuneClientSecret</code></td><td style="text-align:left;">Client Secret for Service Principal authentication</td></tr><tr><td style="text-align:left;"><code>GitHubToken</code></td><td style="text-align:left;">OAuth token for Cloud Sync</td></tr><tr><td style="text-align:left;"><code>AIKey</code></td><td style="text-align:left;">API Key for OpenAI or Gemini</td></tr></tbody></table><h3 id="encrypted-registry-policies" tabindex="-1">Encrypted Registry Policies <a class="header-anchor" href="#encrypted-registry-policies" aria-label="Permalink to &quot;Encrypted Registry Policies&quot;">​</a></h3><p>To deploy secrets via GPO, use the <code>_ENC</code> suffix and AES encryption (see <a href="./CLI_Reference#config">CLI Reference</a>).</p><ul><li>Example: <code>IntuneClientSecret_ENC</code></li></ul><h2 id="setting-values-via-command-line" tabindex="-1">Setting Values via Command Line <a class="header-anchor" href="#setting-values-via-command-line" aria-label="Permalink to &quot;Setting Values via Command Line&quot;">​</a></h2><h3 id="enable-debug-mode" tabindex="-1">Enable Debug Mode <a class="header-anchor" href="#enable-debug-mode" aria-label="Permalink to &quot;Enable Debug Mode&quot;">​</a></h3><div class="language-powershell vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">powershell</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">reg add </span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">&quot;HKCU\\Software\\FaserF\\SwitchCraft&quot;</span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;"> /</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">v DebugMode </span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;">/</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">t REG_DWORD </span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;">/</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">d </span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;">1</span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;"> /</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">f</span></span></code></pre></div><h3 id="set-update-channel-to-beta" tabindex="-1">Set Update Channel to Beta <a class="header-anchor" href="#set-update-channel-to-beta" aria-label="Permalink to &quot;Set Update Channel to Beta&quot;">​</a></h3><div class="language-powershell vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">powershell</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">reg add </span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">&quot;HKCU\\Software\\FaserF\\SwitchCraft&quot;</span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;"> /</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">v UpdateChannel </span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;">/</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">t REG_SZ </span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;">/</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">d </span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">&quot;beta&quot;</span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;"> /</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">f</span></span></code></pre></div><h3 id="disable-winget-integration" tabindex="-1">Disable Winget Integration <a class="header-anchor" href="#disable-winget-integration" aria-label="Permalink to &quot;Disable Winget Integration&quot;">​</a></h3><div class="language-powershell vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">powershell</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">reg add </span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">&quot;HKCU\\Software\\FaserF\\SwitchCraft&quot;</span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;"> /</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">v EnableWinget </span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;">/</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">t REG_DWORD </span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;">/</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">d </span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;">0</span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;"> /</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">f</span></span></code></pre></div><h2 id="managing-via-group-policy" tabindex="-1">Managing via Group Policy <a class="header-anchor" href="#managing-via-group-policy" aria-label="Permalink to &quot;Managing via Group Policy&quot;">​</a></h2><p>See <a href="./PolicyDefinitions/README">ADMX Template</a> for GPO/Intune deployment documentation.</p>',23)])])}const y=t(l,[["render",n]]);export{p as __pageData,y as default};
