import{_ as t,o as e,c as n,ag as o}from"./chunks/framework.D4MlTk4Q.js";const u=JSON.parse('{"title":"SwitchCraft Addon Guide","description":"","frontmatter":{},"headers":[],"relativePath":"ADDONS.md","filePath":"ADDONS.md","lastUpdated":1770603064000}'),s={name:"ADDONS.md"};function i(l,a,r,d,c,p){return e(),n("div",null,[...a[0]||(a[0]=[o(`<h1 id="switchcraft-addon-guide" tabindex="-1">SwitchCraft Addon Guide <a class="header-anchor" href="#switchcraft-addon-guide" aria-label="Permalink to &quot;SwitchCraft Addon Guide&quot;">​</a></h1><p>SwitchCraft supports an addon system to extend its functionality while keeping the core lightweight and compliant with antivirus heuristics (e.g., by separating Advanced/Intune analysis).</p><h2 id="installing-addons" tabindex="-1">Installing Addons <a class="header-anchor" href="#installing-addons" aria-label="Permalink to &quot;Installing Addons&quot;">​</a></h2><h3 id="automatic-installation" tabindex="-1">Automatic Installation <a class="header-anchor" href="#automatic-installation" aria-label="Permalink to &quot;Automatic Installation&quot;">​</a></h3><p>SwitchCraft attempts to install addons automatically in the following order:</p><ol><li><strong>Local Cache</strong>: Checks if the addon is present in <code>addons/</code> folder.</li><li><strong>Current Release</strong>: Downloads the matching zip from the GitHub Release for your version.</li><li><strong>Main Branch</strong>: Falls back to the latest code from the <code>main</code> branch.</li><li><strong>Older Releases</strong>: Scans previous releases for compatible assets.</li></ol><h3 id="manual-installation" tabindex="-1">Manual Installation <a class="header-anchor" href="#manual-installation" aria-label="Permalink to &quot;Manual Installation&quot;">​</a></h3><p>If automatic installation fails, you can:</p><ol><li><strong>Browser Download</strong>: Open the release page and download the addon ZIP manually.</li><li><strong>Custom Upload</strong>: In <code>Settings -&gt; Help -&gt; Addon Manager</code>, use the <strong>&quot;Upload Custom Addon&quot;</strong> button to select the downloaded ZIP file.</li></ol><h2 id="creating-custom-addons" tabindex="-1">Creating Custom Addons <a class="header-anchor" href="#creating-custom-addons" aria-label="Permalink to &quot;Creating Custom Addons&quot;">​</a></h2><p>Addons are standard Python packages located in the <code>addons/</code> directory.</p><h3 id="structure" tabindex="-1">Structure <a class="header-anchor" href="#structure" aria-label="Permalink to &quot;Structure&quot;">​</a></h3><p>A valid addon ZIP must contain a top-level folder matching <code>switchcraft_&lt;name&gt;</code> or have that folder somewhere inside.</p><p><strong>Example Structure:</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>my_addon.zip</span></span>
<span class="line"><span>└── switchcraft_myfeature/</span></span>
<span class="line"><span>    ├── __init__.py      # Required: Entry point</span></span>
<span class="line"><span>    ├── service.py       # Optional: Logic</span></span>
<span class="line"><span>    └── gui/</span></span>
<span class="line"><span>        └── view.py      # Optional: UI components</span></span></code></pre></div><h3 id="development" tabindex="-1">Development <a class="header-anchor" href="#development" aria-label="Permalink to &quot;Development&quot;">​</a></h3><ol><li>Clone the SwitchCraft repository.</li><li>Create a folder <code>src/switchcraft_&lt;name&gt;</code>.</li><li>Implement your logic.</li><li>To distribute, zip the folder <code>switchcraft_&lt;name&gt;</code>.</li></ol><h3 id="key-addon-types" tabindex="-1">Key Addon Types <a class="header-anchor" href="#key-addon-types" aria-label="Permalink to &quot;Key Addon Types&quot;">​</a></h3><ul><li><strong>Advanced</strong>: Provides Intune Graph API and Brute-force analysis tools.</li><li><strong>Winget</strong>: Provides Winget CLI wrapper and repository search.</li><li><strong>AI</strong>: Provides generic AI assistant interfaces.</li></ul><h2 id="security-warning" tabindex="-1">Security Warning <a class="header-anchor" href="#security-warning" aria-label="Permalink to &quot;Security Warning&quot;">​</a></h2><div class="warning custom-block github-alert"><p class="custom-block-title">WARNING</p><p>Only install addons from trusted sources (e.g., the official GitHub repository). Custom addons run with the same privileges as SwitchCraft.</p></div>`,21)])])}const g=t(s,[["render",i]]);export{u as __pageData,g as default};
